class StackUsingQueue:

    def __init__(self):

        self.q1 = []
        self.q2 = []

    def isEmpty(self):

        return bool(not self.q1 and not self.q2)

    def peek(self):

        if self.isEmpty():
            raise Exception("Stack Underflow!")

        qTemp = Queue()

        while not self.q1.empty():

            tempItem = self.q1.dequeue(); 
            qTemp.enqueue(tempItem)

        while not qTemp.isEmpty():

            itemToReturn = qTemp.dequeue()
            self.q2.enqueue(itemToReturn)

        return itemToReturn

    def pop(self):

        if self.isEmpty():
            raise Exception("Stack underflow!")

        qTemp = Queue()

        while not self.q1.empty():

            tempItem = self.q1.dequeue();  
            qTemp.enqueue(tempItem)

        while not qTemp.isEmpty():

            itemToPop = qTemp.dequeue()
            self.q2.enqueue(itemToPop)

        lastElementInQ2 = self.q2.peek()
        self.q2.dequeue()
        return lastElementInQ2


    def push(self, element):

        self.q1.enqueue(element)

