def numberOfWaysToMakeChangeRecursive(amountLeft, coinValuesSortedDescending):
    '''Assumes amountLeft >= 0'''
    numCoinsUsed = 0
    if amountLeft == 0:
        return 1
    elif amountLeft &lt; 0:
        return 0
    else:
        for nextCoinValue in coinValuesSortedDescending:
            numWaysWithNextCoin = \
                numberOfWaysToMakeChangeRecursive(\
                    amountLeft - nextCoinValue,\
                        coinValuesSortedDescending)\
                            +\
                                numberOfWaysToMakeChangeRecursive(\
                                    amountLeft,\
