def rot13(message):

    """Rotates message by 13 characters."""

    newMessage = []

    for char in message:

        if char!='':

            index = ord(char) - ord('a')

            if index >= 0:

                newIndex = (index+13)%26

                newChar = chr(ord('a') +newIndex)

            elif index <= (-1*26):

                newIndex = abs(((index)+13)%26)

                newChar = chr(ord('z') +newIndex)



            newMessage += newChar

        else:

            newMessage+=''

    return ''.join(newMessage).lower()


