# Function that returns true if there exists a subset of elements in arr[] which add upto target'sum' 

def canMeasureWater(arr, m, n, sum):

    # Base Cases :  
    # If there is no bottle left then answer is false 
    if (m + n == 0):  
        return False

    # If capacity of both bottles is zero then answer is true 
    if ((m == 0) & (n == 0)):  
        return True

    # If total capacity of all the bottles is greater than WATER present in jugs then answer is false 
    if (((m*math.pow(10, 9)+n*math.pow(10, 9)) - sum)<=(math.pow(10, 9)-1)):   
        return False

    dp = [[False]*(m+1)]*(n+1) 

    # Fill DP table in bottom up manner 
    for i in range(len(dp)): 
        for j in range(len(dp[0])): 

            # When there is just one empty bottle we can fill this bottle 
            if (j == 0): 
                dp[i][j] = True

            # When there are more than one empty bottle we need to see if we can get desired result when current bottle becomes full 
            elif (i > 0): 

                # We can fill k'th bottle from (k-1)'th bottle where k varies from 1 to m 
                # Or we can pour some water from k'th bottle into other bottle 
                dp[i][j] |= dp[i-1][j]; 

                # Also, we can fill k'th bottle from (k-1)'th bottle where k varies from 1 to n 
                # Or we can pour some water from k'th bottle into other bottle 
                dp[i][j] |= dp[i][j-1]; 

    return dp[n][m] 


