def binarySearch(array, targetValue):

    lowIndex = 0
    highIndex = len(array) - 1


    while lowIndex<=highIndex:

        midPoint = floor((lowIndex + highIndex)/2)

        valueAtMidpoint = array[midPoint]

        if valueAtMidpoint==targetValue:
                return midPoint

        elif valueAtMidpoint<targetValue:
                lowIndex = midPoint + 1

        else: 
                highIndex = midPoint - 1

    return None


