def getKnapsackValue(wt, val, W, n):
    dp=[[-float('inf')]*W for _ in range(n+1)]

    # Build table K[][] in bottom up manner
    for i in range(n+1):
        for w in range(W+1):
            if wt[i-1] <=w :
                dp[i][w]=max(val[i-1] +dp[i-1][w-wt[i-1]], dp[i-1][w])
            else:
                dp[i][w]=dp[i-1][w]

    print("Table")
    for row in dp:
        print(*row)

    return dp[n][W]


