# Function to check if maze exists 
def HasPath(maze, sol, sr, sc, dr, dc): 
    if ((sr == dr) and (sc == dc)): 
        return True

    # Check if destination cell is  
    # already visited 
    if (sol[dr][dc]): 
       return False

    # Mark the current cell as visited 
    sol[dr][dc]= True

    # Move forward in all four directions 
    res = HasPath(maze, sol, sr, sc+1, dr, dc) 

    if (not res): 
      res = HasPath(maze, sol, sr+1, sc, dr, dc) 

    if (not res): 
      res = HasPath(maze, sol, sr, sc-1, dr, dc)

    if (not res): 
      res = HasPath(maze, sol, sr-1, sc, dr, dc)

    return res 


