def countWaysToScorePoints(totalPointsToBeScored,scorePerAttempt,numberOfAttempts ):

    dp=[[-1]*(totalPointsToBeScored+1)]*(numberOfAttempts+1)

    def solve(attemptNumber, totalPointsSoFar ) -> int:

        if attemptNumber<=0 or totalPointsSoFar <0:

            return -1

        if dp[attemptNumber][totalPointsSoFar ]!=-1:

            return dp[attemptNumber][totalPointsSoFar ]

        if totalPointsSoFar >= numberOfAttempts * scorePerAttempt:

            return 1

        result=solve(attemptNumber-1,totalPointsSoFar+(scorePerAttempt))

        if totalPointsSoFar>=scorePerAttempt:

            tempResult=solve(attemptNumber-1,totalPointsSoFar-(scorePerAttempt))

           if tempResult!= -1:

                result+=tempResult

        dp[attemptNumber][totalPointsSoFar ]=result

        return result

    return solve(numberOfAttempts,totalPointsToBeScored)





