def BFS(graph,startVertex): 

    exploredVertices = []

    queueOfVerticesToExplore =deque([startVertex])

    while queueOfVerticesToExplore:

        frontierVertex = queueOfVerticesToExplore.popleft();

        if frontierVertex not in exploredVertices:

            neighbours = graph[frontierVertex]; 

            for neighbour in neighbours:

                queueOfVerticesToExplore.append(neighbour);

            exploredVertices.append(frontierVertex);

    return exploredVertices;

