        heappush(self.__maxHeap,(-heappop(self.__minHeap)[0],))


        if abs((len(self.__minHeap)-len(self.__maxHeap)))>1:

            valToPop=-heappop(self.__maxHeap)[0]
            heappush(self.__minHeap,(valToPop,))


        elif len(self.__minHeap)>len(self.__maxHeap)+1:

            valToPush=heappop(self.__minHeap)[0]
            heappush(self.__maxHeap,(-valToPush,))

    @property
    def median(self)->float:


        if ((len(self.__minHeap)%2)!=0):

            return (-self.__minHeap[0][0])/(len(self.__minHeap)/2)

        else:

            minVal=self.__minHeap[0][0]
            maxVal=-self.__maxHeap[0][0]
            return (minVal/2)+(maxVal/2)





