public class TestMarksCalculator {

    static Map<Integer, Integer> mapOfOptionIdAndItsCountInAnswerKey = new HashMap<>();

    public void addOptionsToHashMap(String[] optionsArrayWithIdsAsStrings){

        Arrays.stream(optionsArrayWithIdsAsStrings ).forEach((optionAsString)->{

            String[] splittedOption = optionAsString.split("#");

            final int optionId = Integer.parseInt(splittedOption[0]);

            final int countOfThisOptionInTheAnswerKey = Integer.parseInt(splittedOption[1]);

            mapOfOptionIdAndItsCountInAnswerKey.putIfAbsent(optionId,countOfThisOptionInTheAnswerKey);

        });

    }


    public Set<Integer> getUserSelectedAnswersSet(){

        Scanner scanner = new Scanner(System.in);

        System.out.println("\nEnter your selection separated by space:");

        String userInput = scanner.nextLine();

        HashSet<Integer> resultantUserSelectionSet = new LinkedHashSet<>(Arrays.asList(userInput.trim().split("\\s")));

        return resultantUserSelectionSet;

    }


    public double computeTestScore(Set<Integer> userSelectionsSet){

        Double score = ((double)(mapOfOptionIdAndItsCountInAnswerKey.keySet()).size())/2d*100;

        Iterator iterator = userSelectionsSet.iterator();

        while(iterator.hasNext()){

            Object object = iterator.next();

            try{

                Integer integerObject = Integer.valueOf("" +object);

                score += (((double)((Double)mapOfOptionIdAndItsCountInAnswerKey.getOrDefault(integerObject,-99))).doubleValue()/(((double)(mapOfOptionIdAndItsCountInAnswerKey.get(integerObject)).intValue())))*100;

            }catch(Exception e){}

        }

        return Math.round(score)/100;

    }


