import torch
class SimpleRNNCell():

    def __init__(self, num_units, activation='tanh'):

        self._num_units = num_units 
        self._activation = getattr(torch, activation) 

    @property
    def state_size(self):

        return self._num_units

    @property
    def output_size(self):

        return self._num_units 

    def forward(self, inputs, states):

        prev_state = states[-1]  
        next_input = torch.addmm(prev_state,inputs,(states*0)+1.)   
        next_state = self._activation(next_input) 

        outputs = []
        outputs.append(next_state)
        return tuple(outputs), next_state

